pragma solidity ^0.4.24;
pragma experimental ABIEncoderV2;

contract Mapping {
    event UpdateShare(
        string _oldHash,
        string _newHash
    );

    event RemoveShare(
        string _hash
    );

    event CreateShare(
        string _hash
    );

    event CreatePublicKey(
        string _pubKey
    );

    struct Share {
        string hash;
        string name;
        address[] receivers;
        address owner;
    }

    Share[] public shares;
    mapping(address => uint[]) public receivers;
    mapping(address => uint[]) public senders;

    // RSA-pubkey-hashstore
    mapping(address => string) public rsaHashStore;

    // ////////////////////////////
    // public functions
    // ////////////////////////////

    // returns the index of the created share
    function createShare(string newHash, string newName) public returns (uint) {
        uint index = shareExists(newHash, newName);
        if (index != uint(-1)) {
            return index;
        }
        address[] memory noReceivers;
        Share memory newShare = Share({
            hash: newHash,
            name: newName,
            receivers: noReceivers,
            owner: msg.sender
        });
        index = shares.push(newShare) - 1;
        senders[msg.sender].push(index);
        emit CreateShare(newHash);
        return index;
    }

    function getShareHash(uint index) public view returns (string) {
        return shares[index].hash;
    }

    function getHashForUint(uint index) public view returns (string) {
        return shares[index].hash;
    }

    function getShareName(uint index) public view returns (string) {
        return shares[index].name;
    }

    function getNameForUint(uint index) public view returns (string) {
        return shares[index].name;
    }

    function getShareReceivers(uint index) public view returns (address[]) {
        return shares[index].receivers;
    }

    // returns shares I got
    function getReceivedShares() public view returns(uint[]) {
        return receivers[msg.sender];
    }
    // returns shares I created
    function getSentShares() public view returns(uint[]) {
        return senders[msg.sender];
    }

    // adds a new receiver to share and updates hash
    function addToShareAndUpdateHash(uint index, address receiver, string newHash) public {
        revertIfNotMyShare(index);
        addToShare(index, receiver);
        string memory oldHash = shares[index].hash;
        shares[index].hash = newHash;
        emit UpdateShare(oldHash, newHash);
    }

    // adds a new receiver to share
    function addToShare(uint index, address receiver) public {
        revertIfNotMyShare(index);

        if (!hasAlreadyReceived(index, receiver)) {
            shares[index].receivers.push(receiver);
            receivers[receiver].push(index);
        }
    }

    // remove share
    function removeShare(uint index) public {
        revertIfNotMyShare(index);
        string memory removedHash = shares[index].hash;
        // remove share from all receivers mapping
        for (uint i = 0; i < shares[index].receivers.length; i++) {
            removeFromReceiversMapping(index, shares[index].receivers[i]);
        }
        // remove share from senders share list
        removeShareFromSender(index);
        // remove the share
        address[] memory noReceivers;
        Share memory emptyShare = Share({
            hash: "",
            name: "",
            receivers: noReceivers,
            owner: address(0)
        });
        shares[index] = emptyShare;
        emit RemoveShare(removedHash);
    }

    // revoke rights for receicer and update ipfs hash
    function revokeShareAndUpdateHash(uint index, address receiver, string newHash) public {
        revertIfNotMyShare(index);
        removeFromReceivers(index, receiver);
        string memory oldHash = shares[index].hash;
        shares[index].hash = newHash;
        emit UpdateShare(oldHash, newHash);
    }

    // updates ipfs hash of a share
    function updateHash(uint index, string newHash) public {
        revertIfNotMyShare(index);
        string memory oldHash = shares[index].hash;
        shares[index].hash = newHash;
        emit UpdateShare(oldHash, newHash);
    }

    // updates name of a share
    function updateName(uint index, string newName) public {
        revertIfNotMyShare(index);
        shares[index].name = newName;
    }

    function getIndexFor(string hash, string name) public view returns (uint) {
        for (uint i = 0; i < senders[msg.sender].length; i++) {
            if (isEqual(shares[i].hash, hash) && isEqual(shares[i].name, name)) {
                return i;
            }
        }
        return uint(-1);
    }

    // RSA pub key operations

    function setPubKeyHash(string pubkey) public {
        rsaHashStore[msg.sender] = pubkey;
        emit CreatePublicKey(pubkey);
    }
    function getPubKeyHashFor(address adr) public view returns (string) {
        return rsaHashStore[adr];
    } 
    function getPubKeyHash() public view returns (string) {
        return rsaHashStore[msg.sender];
    }

    function getReceivedSharesFor(address a) public view returns (uint[]) {
        return receivers[a];
    }

    // ////////////////////////////
    // private functions
    // ////////////////////////////

    // removes a receiver from a share
    function removeFromReceivers(uint index, address receiver) private {
        removeFromReceiversMapping(index, receiver);
        removeFromSharesList(index, receiver);
    }

    // checks if receiver aleardy received share
    function hasAlreadyReceived(uint index, address receiver) private view returns (bool) {
        uint[] storage received = receivers[receiver];
        for (uint j = 0; j < received.length; j++) {
            if (received[j] == index) {
                return true;
            }
        }
        return false;
    }

    function getShareIndexForSenderMapping(uint index) private view returns (uint) {
        for (uint i = 0; i < senders[msg.sender].length; i++) {
            if (index == senders[msg.sender][i]) {
                return i;
            }
        }
        return uint(-1);
    }

    function removeShareFromSender(uint index) private {
        uint indexOfShare = getShareIndexForSenderMapping(index);
        if (indexOfShare != uint(-1)) {
            for (uint i = indexOfShare; i < senders[msg.sender].length-1; i++) {
                senders[msg.sender][i] = senders[msg.sender][i+1];
            }
            senders[msg.sender].length--;
        }
    }

    // removes a receiver from receivers mapping
    function removeFromReceiversMapping(uint index, address receiver) private {
        uint indexOfShare = getIndexOfShareForReceiversMapping(index, receiver);
        if (indexOfShare != uint(-1)) {
            removeShareIndexFromReceiversMapping(indexOfShare, receiver);
        }
    }

    function getIndexOfShareForReceiversMapping(uint index, address receiver) private view returns (uint) {
        for (uint i = 0; i < receivers[receiver].length; i++) {
            if (receivers[receiver][i] == index) {
                return i;
            }
        }
        return uint(-1);
    }

    function removeShareIndexFromReceiversMapping(uint index, address receiver) private {
        for (uint i = index; i < receivers[receiver].length-1; i++) {
            receivers[receiver][i] = receivers[receiver][i+1];
        }
        receivers[receiver].length--;
    }

    // removes a receiver from share list
    function removeFromSharesList(uint index, address receiver) private {
        uint indexForReceiverAddress = getIndexForReceiverAddress(index, receiver);
        if (indexForReceiverAddress != uint(-1)) {
            for (uint i = indexForReceiverAddress; i < shares[index].receivers.length-1; i++) {
                shares[index].receivers[i] = shares[index].receivers[i+1];
            }
            shares[index].receivers.length--;
        }
    }

    // returns index of share receiver
    function getIndexForReceiverAddress(uint index, address receiver) private view returns (uint) {
        for (uint i = 0; i < shares[index].receivers.length; i++) {
            if (receiver == shares[index].receivers[i]) {
                return i;
            }
        }
        return uint(-1);
    }

    // checks if share with hash and name already exists
    function shareExists(string hash, string name) private view returns (uint) {
        for (uint i = 0; i < shares.length; i++) {
            if (isEqual(shares[i].hash, hash) && isEqual(shares[i].name, name)) {
                return i;
            }
        }
        return uint(-1);
    }

    // checks if both strings are equal
    function isEqual(string a, string b) private pure returns (bool) {
        return keccak256(bytes(a)) == keccak256(bytes(b));
    }

    // check if share for given index is owned by msg.sender
    function revertIfNotMyShare(uint index) private view {
        uint[] memory sent = senders[msg.sender];
        bool myShare = false;
        for (uint i = 0; i < sent.length; i++) {
            if (sent[i] == index) {
                myShare = true;
                break;
            }
        }
        if (!myShare) {
            revert("sorry! it's not your share!");
        }
    }

