pragma solidity ^0.4.24;

contract Shares {
    string[] public shareToHashes;
    mapping(address => uint[]) public ownerToShares;
    mapping(uint=>address[]) public sharesToAuthors;

    event Updated(
        uint _pointer,
        string _oldHash,
        string _newHash,
        address _updater
    );
    event Deleted(
        uint _pointer,
        address _deleter
    );
    event AuthorAdded(
        uint _pointer,
        address _newAuthor,
        address _owner
    );
    event AuthorDeleted(
        uint _pointer,
        address _removedAuthor,
        address _owner
    );

    function createShare(string newHash) public returns (uint){
        uint pointer = shareToHashes.push(newHash) - 1;
        ownerToShares[msg.sender].push(pointer);
        emit Updated(pointer, "", newHash, msg.sender);
        return pointer;
    }
    function removeShare(uint pointer) public {
        uint index = revertIfNotMine(pointer);

        // delete hash
        string memory oldHash = shareToHashes[pointer];
        shareToHashes[pointer] = "";

        // removeFromOwner
        for (uint i = index; index < ownerToShares[msg.sender].length-1; i++){
            ownerToShares[msg.sender][i] = ownerToShares[msg.sender][i+1];
        }
        ownerToShares[msg.sender].length--;

        // removeFromAuthors
        sharesToAuthors[pointer] = new address[](0);

        emit Updated(pointer, oldHash, "", msg.sender);
    }
    function updateHash(uint pointer, string clientOldHash, string newHash) public {
        revertIfNotAllowed(pointer);

        string memory oldHash = shareToHashes[pointer];
        require(equals(oldHash, clientOldHash), "mid air collision, your provided oldhash is already outdated");

        shareToHashes[pointer] = newHash;

        emit Updated(pointer, oldHash, newHash, msg.sender);
    }
    function addAuthor(uint pointer, address newAuthor) public {
        revertIfNotMine(pointer);
        address[] memory authors = sharesToAuthors[pointer];
        bool found = false;
        for (uint i = 0; i < authors.length; i++){
            if (authors[i] == newAuthor){
                found = true;
                break;
            }
        }
        if (found){
            revert("given address is already in the list of authors");
        }
        sharesToAuthors[pointer].push(newAuthor);
        emit AuthorAdded(pointer, newAuthor, msg.sender);
    }
    function removeAuthor(uint pointer, address author) public {
        revertIfNotMine(pointer);
        uint index = findAuthorIndex(pointer, author);
        for (uint i = index; i < sharesToAuthors[pointer].length-1; i++){
            sharesToAuthors[pointer][i] = sharesToAuthors[pointer][i+1]; 
        }
        sharesToAuthors[pointer].length--;
        emit AuthorDeleted(pointer, author, msg.sender);
    }
    function getAuthorsForShare(uint pointer) public view returns (address[]) {
        return sharesToAuthors[pointer];
    }
    function findAuthorIndex(uint pointer, address author) private view returns (uint) {
        for (uint i = 0; i < sharesToAuthors[pointer].length; i++){
            if (sharesToAuthors[pointer][i] == author){
                return i;
            }
        }
        revert ("given address is not in the list of authors");
    }

    function getMySharePointers() public view returns (uint[]) {
        return ownerToShares[msg.sender];
    } 
    function getHash(uint pointer) public view returns (string hash1){
        return shareToHashes[pointer];
    }
    function getHashForTwo(uint pointer1, uint pointer2) public view returns (string hash1, string hash2){
        return (shareToHashes[pointer1], shareToHashes[pointer2]);
    }
    function getHashForThree(uint pointer1, uint pointer2, uint pointer3) public view returns (string hash1, string hash2, string hash3){
        return (shareToHashes[pointer1], shareToHashes[pointer2], shareToHashes[pointer3]);
    }
    function getHashForFour(uint pointer1, uint pointer2, uint pointer3, uint pointer4) public view returns (string hash1, string hash2, string hash3, string hash4){
        return (shareToHashes[pointer1], shareToHashes[pointer2], shareToHashes[pointer3], shareToHashes[pointer4]);
    }
    function getHashForFive(uint pointer1, uint pointer2, uint pointer3, uint pointer4, uint pointer5) public view returns (string hash1, string hash2, string hash3, string hash4, string hash5){
        return (shareToHashes[pointer1], shareToHashes[pointer2], shareToHashes[pointer3], shareToHashes[pointer4], shareToHashes[pointer5]);
    }
    function getHashForSix(uint pointer1, uint pointer2, uint pointer3, uint pointer4, uint pointer5, uint pointer6) public view returns (string hash1, string hash2, string hash3, string hash4, string hash5, string hash6){
        return (shareToHashes[pointer1], shareToHashes[pointer2], shareToHashes[pointer3], shareToHashes[pointer4], shareToHashes[pointer5], shareToHashes[pointer6]);
    }

    function revertIfNotMine(uint pointer) public view returns (uint) {
        uint[] memory myShares = ownerToShares[msg.sender];
        for (uint i = 0; i < myShares.length; i++){
            if (myShares[i] == pointer){
                return i;
            }
        }
        revert("sorry, you are not the owner of this share");
    }
    function revertIfNotAllowed(uint pointer) public view {
        // check for owner
        uint[] memory myShares = ownerToShares[msg.sender];
        for (uint i = 0; i < myShares.length; i++){
            if (myShares[i] == pointer){
                return;
            }
        }
        // check for author
        address[] memory authors = sharesToAuthors[pointer];
        for (uint j = 0; j < authors.length; j++){
            if (authors[j] == msg.sender){
                return;
            }
        }
        revert("sorry, you are not allowed to modify this share");
    }

    function getShareCount() public view returns (uint) {
        return shareToHashes.length;
    }

    // Invites here - should have been in a seperate contract :(

    event NewInvite(
        uint _pointer,
        address _receiver,
        address _sender
    );

    mapping(address => uint[]) walletToInvites;

    function invite(uint pointer, address receiver) public {
        emit NewInvite(pointer, receiver, msg.sender);
        revertIfNotAllowed(pointer);
        walletToInvites[receiver].push(pointer);
    }

    function getInvitesFor(address receiver) public view returns (uint[]) {
        return walletToInvites[receiver];
    }
    function getMyInvites() public view returns (uint[]) {
        return walletToInvites[msg.sender];
    }
    function equals(string a, string b) public pure returns (bool) {
        return keccak256(bytes(a)) == keccak256(bytes(b));
    }
}
