pragma solidity ^0.4.24;

contract SingleFiles{
    uint userCount = 0;
    mapping(address => string) public walletToKey;
    event KeyAssigned(
        address _owner,
        string _hash
    );
    function setPubKeyHash(string pubKeyHash) public {
        string memory current = walletToKey[msg.sender];
        if (bytes(current).length == 0){
            userCount++;
        }
        walletToKey[msg.sender] = pubKeyHash;
        emit KeyAssigned(msg.sender, pubKeyHash);
    }
    function getPubKeyHashFor(address wallet) public view returns (string) {
        return walletToKey[wallet];
    }
    function getMyPubKeyHash() public view returns (string) {
        return walletToKey[msg.sender];
    }
    function getUserCount() public view returns (uint) {
        return userCount;
    }

    mapping(address => string) public walletToSettings;
    event SettingsAssigned(
        address _owner,
        string _oldHash,
        string _newHash
    );
    function setSettingsHash(string newHash) public {
        string memory oldHash = walletToSettings[msg.sender];
        walletToSettings[msg.sender] = newHash;
        emit SettingsAssigned(msg.sender, oldHash, newHash);
    }
    function getSettingsHash() public view returns (string) {
        return walletToSettings[msg.sender];
    }

    string[] public groups;
    mapping(uint => address[]) public groupToAuthors;
    mapping(address => uint[]) public authorToGroups;
    event NewHashForGroup(
        uint _groupId,
        string _oldHash,
        string _newHash,
        address _sender
    );
    event NewAuthorForGroup(
        uint _groupId,
        address _newAuthor,
        address _sender
    );
    event RemovedAuthorForGroup(
        uint _groupId,
        address _removedAuthor,
        address _sender
    );
    function createGroup(string newHash) public returns (uint){
        uint pointer = groups.push(newHash) - 1;
        groupToAuthors[pointer].push(msg.sender);
        authorToGroups[msg.sender].push(pointer);
        emit NewHashForGroup(pointer, "", newHash, msg.sender);
        return pointer;
    }
    function updateHashForGroup(uint pointer, string oldClientHash, string newHash) public {
        if (!isAuthor(pointer)){
            revert("you are not allowed to change this group");
        }
        string memory oldHash = groups[pointer];
        require(equals(oldHash, oldClientHash), "mid air collision, your provided oldhash is already outdated");
        groups[pointer] = newHash;
        emit NewHashForGroup(pointer, oldHash, newHash, msg.sender);
    }
    function deleteGroup(uint pointer) public {
        if (!isAuthor(pointer)){
            revert("you are not allowed to delete this group");
        }
        groups[pointer] = "";
        for (uint i = 0; i < groupToAuthors[pointer].length; i++){
            address authorToRemove = groupToAuthors[pointer][i];
            uint index = getIndexOfGroup(pointer, authorToRemove);
            for (uint j = index; j < authorToGroups[authorToRemove].length-1; j++){
                authorToGroups[authorToRemove][j] = authorToGroups[authorToRemove][j+1];
            }
            authorToGroups[authorToRemove].length--;
        }
        groupToAuthors[pointer] = new address[](0);
    }
    function getMyGroups() public view returns (uint[]) {
        return authorToGroups[msg.sender];
    }
    function getGroupsFor(address wallet) public view returns (uint[]) {
        return authorToGroups[wallet];
    }
    function getGroupCount() public view returns (uint) {
        return groups.length;
    }
    function getHashForGroup(uint groupId) public view returns (string){
        return groups[groupId];
    }
    function getAuthorsForGroup(uint groupId) public view returns (address[]){
        return groupToAuthors[groupId];
    }
    function addAuthor(uint pointer, address newAuthor) public {
        if (!isAuthor(pointer)){
            revert("you are not allowed to modify this group");
        }
        groupToAuthors[pointer].push(newAuthor);
        authorToGroups[newAuthor].push(pointer);
        emit NewAuthorForGroup(pointer, newAuthor, msg.sender);
    }
    function deleteAuthor(uint pointer, address author) public {
        if (!isAuthor(pointer)){
            revert("you are not allowed to modify this group");
        }
        uint index = getIndexOfAuthor(pointer, author);
        uint otherIndex = getIndexOfGroup(pointer, author);
        for (uint i = index; i < groupToAuthors[pointer].length-1; i++){
            groupToAuthors[pointer][i] = groupToAuthors[pointer][i+1];
        }
        groupToAuthors[pointer].length--;
        for (uint j = otherIndex; j < authorToGroups[author].length-1; j++){
            authorToGroups[author][j] = authorToGroups[author][j+1];
        }
        authorToGroups[author].length--;
        emit RemovedAuthorForGroup(pointer, author, msg.sender);
    }
    function isAuthor(uint pointer) public view returns (bool) {
        for (uint i = 0; i < groupToAuthors[pointer].length; i++){
            if (groupToAuthors[pointer][i] == msg.sender){
                return true;
            }
        }
        return false;
    } 
    function getIndexOfAuthor(uint pointer, address author) public view returns (uint) {
        for (uint i = 0; i < groupToAuthors[pointer].length; i++){
            if (groupToAuthors[pointer][i] == author){
                return i;
            }
        }
        revert("address is not a known author");
    }
    function getIndexOfGroup(uint pointer, address author) public view returns (uint) {
        for (uint i = 0; i < authorToGroups[author].length; i++){
            if (authorToGroups[author][i] == pointer){
                return i;
            }
        }
        revert("pointer is not in authors grouplist");
    }
    function equals(string a, string b) public pure returns (bool) {
        return keccak256(bytes(a)) == keccak256(bytes(b));
    }
}
